import React, { useState, useEffect, useRef } from 'react';
import { Badge, Box, Grid, Heading } from "@chakra-ui/react";
import moment from 'moment'

const CardContainer = ({ data }) => {
   return (
    <Grid templateColumns="repeat(4, 1fr)" gap={6}>
      {data.map((e) => (
        <Box maxW="md" borderWidth="2px" borderRadius="lg" overflow="hidden">
          <Box p="6">
            <Box d="flex" alignItems="baseline">
              <Badge borderRadius="full" px="2" colorScheme="teal">
                {moment(e.properties.time).format('L')}
              </Badge>
              <Box
                color="gray.500"
                fontWeight="semibold"
                letterSpacing="wide"
                fontSize="xs"
                textTransform="uppercase"
                ml="2"
              >
                {e.properties.alert}
              </Box>
            </Box>
          <Box mt="1" paddingTop="0.5em" fontSize="xl" fontWeight="semibold" as="h4" lineHeight="tight">
            {e.properties.title}
          </Box>

          <Box d="flex" justifyContent="center" alignItems="flex-end">
            {e.properties.felt}
            <Box d="flex" justifyContent="center" alignItems="flex-end" className="Felt" as="span" color="blue.600" fontSize="md">
              people felt
            </Box>
          </Box>
        </Box>
      </Box>
      ))}
    </Grid>
  )
}

export default CardContainer;
